import React from "react";
import Modal from '@material-ui/core/Modal';
import withStyles from '@material-ui/core/styles/withStyles';
import SignUp from '../../components/SignUp';
import PropTypes from "prop-types";
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import axios from "axios";



function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

const styles = theme => ({
    modal: {
        position: 'absolute',
        width: theme.spacing.unit * 50,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
        outline: 'none',
    },
    button: {
        marginTop: theme.spacing.unit * 3,
        marginLeft: theme.spacing.unit,
        display: 'flex',
        justifyContent: 'center',
    },
    layout: {
        width: 'auto',
        marginLeft: theme.spacing.unit * 2,
        marginRight: theme.spacing.unit * 2,
        [theme.breakpoints.up(600 + theme.spacing.unit * 2 * 2)]: {
            width: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
        },
    },
    paper: {
        marginTop: theme.spacing.unit * 3,
        marginBottom: theme.spacing.unit * 3,
        padding: theme.spacing.unit * 2,
        [theme.breakpoints.up(600 + theme.spacing.unit * 3 * 2)]: {
            marginTop: theme.spacing.unit * 6,
            marginBottom: theme.spacing.unit * 6,
            padding: theme.spacing.unit * 3,
        },
    },
});



class signuppage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            companyCode: '',
            userName: '',
            email: '',
            password: '',
            rePassWord: ''
        };
    }

    handleSubmit = (event) => {
        event.preventDefault();
        axios.post('/register', this.state).then((req, res) => {

        })

    };

    handleChange = (event) => {
        const {name , value} = event.target;
        this.setState({
            [name] : value
        })
    };

    render () {
        const { classes } = this.props;

        return (
            <React.Fragment>
            <CssBaseline />
            <main className={classes.layout}>
            <Paper className={classes.paper}>
            <SignUp updateval={this.handleChange.bind(this)} CC={this.state.companyCode} UN={this.state.userName} PW={this.state.password} REPW={this.state.rePassWord} email1={this.state.email}/>

            {(this.state.password === this.state.rePassWord) && (this.state.password !== '') &&
            <Button
                variant="contained"
                color="primary"
                // onClick={this.handleNext}
                className={classes.button}
            >
                Sign Up!
            </Button>


            }
            </Paper>
            </main>
        </React.Fragment>
        )

    }

}

signuppage.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(signuppage);
