import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import SimpleLineChart from '../../components/SimpleLineChart';
import SimpleTable from '../../components/SimpleTable';
import Navbar from '../../components/Navbar2';
import axios from 'axios';


const drawerWidth = 240;

const styles = theme => ({
    root: {
        display: 'flex',
    },
    toolbar: {
        paddingRight: 24, // keep right padding when drawer closed
    },
    toolbarIcon: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: '0 8px',
        ...theme.mixins.toolbar,
    },
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    menuButton: {
        marginLeft: 12,
        marginRight: 36,
    },
    menuButtonHidden: {
        display: 'none',
    },
    title: {
        flexGrow: 1,
    },
    drawerPaper: {
        position: 'relative',
        whiteSpace: 'nowrap',
        overflow: 'auto',
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    drawerPaperClose: {
        overflowX: 'hidden',
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        width: theme.spacing.unit * 7,
        [theme.breakpoints.up('sm')]: {
            width: theme.spacing.unit * 9,
        },
    },
    appBarSpacer: theme.mixins.toolbar,
    content: {
        flexGrow: 1,
        padding: theme.spacing.unit * 3,
        height: '100vh',
        overflow: 'auto',
    },
    chartContainer: {
        marginLeft: -22,
    },
    tableContainer: {
        height: 320,
    },
    h5: {
        marginBottom: theme.spacing.unit * 2,
    },
});

let holderArray = [];
let holderArrayTable = [];

class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {data: null, dataTable: null};
    };

    componentWillMount()
     {
        const headerObj = {
            'Authorization': "bearer " + sessionStorage.getItem("token")
        };

        let holderObject = {};
        // this.setState({compCode: this.props.companyname});
        const dataObj = {company: this.props.companyname};

        axios.post('/api/consumed', dataObj, {headers: headerObj}).then((res) => {
            if(res.data.data !== null) {
                console.log('Data that is coming back from the consumption, ', res.data.data[0]);
                //have the array built here

                for (let i = 0; i < res.data.data[0].length; i++) {
                    holderObject = {
                        name: res.data.data[0][i].Month,
                        Consumed: res.data.data[0][i].Consumed,
                        Received: res.data.data[0][i].Received
                    };
                    // holderArray = this.state.data;
                    holderArray.push(holderObject);
                }
            this.setState({data: holderArray});
            }

            console.log('Holder Array for the data, ', this.state.data);
        }).catch((err) => {
            console.log('Error: ', err);
        });

        axios.post('/api/consumedTable', dataObj, {headers: headerObj}).then((res) => {
            let holderObjectTable = {};
            if(res.data.data !== null) {
                console.log('Data that is coming back from the consumption, ', res.data.data[0]);
                //have the array built here

                for (let i = 0; i < res.data.data[0].length; i++) {
                    holderObjectTable = {
                        name: res.data.data[0][i].PART,
                        quantity: res.data.data[0][i].quantity
                    };
                    // holderArray = this.state.data;
                    holderArrayTable.push(holderObjectTable);
                }
                this.setState({dataTable: holderArrayTable});
            }
        })
    };



    // state = {
    //     open: false,
    // };
    render() {
        console.log('Holder Array for the data,1 ', this.state.data);

        const { classes } = this.props;

        return (

                <div className={classes.root}>
                <CssBaseline/>
                < Navbar handleSignOut={this.props.handleSignOut} username={this.props.companyname}/>
                <main className={classes.content}>
                <div className={classes.appBarSpacer}/>
                <Typography variant="h4" gutterBottom component="h2">
                Orders
                </Typography>
                <Typography component="div" className={classes.chartContainer}>
                    {this.state.data ? <SimpleLineChart passData={holderArray}/> : <Typography variant="h4" gutterBottom component="h2"> Loading..</Typography>}
                {/*<SimpleLineChart passData={holderArray}/>*/}
                </Typography>
                <Typography variant="h4" gutterBottom component="h2">
                Products
                </Typography>
                <div className={classes.tableContainer}>
                    {this.state.dataTable ? <SimpleTable dataPassed={holderArrayTable}/> : <Typography variant="h4" gutterBottom component="h2"> Loading..</Typography>}
                </div>
                </main>
                </div>
        )
    }
}

Dashboard.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Dashboard);
