import React from "react";
import Modal from '@material-ui/core/Modal';
import Typography from '@material-ui/core/Typography';
import withStyles from '@material-ui/core/styles/withStyles';
import SignUp from '../../components/SignUp';
import Button from "../ClientCheckout/clientCKout";
import PropTypes from "prop-types";



function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

const styles = theme => ({
    modal: {
        position: 'absolute',
        width: theme.spacing.unit * 50,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
        outline: 'none',
    },
    button: {
        marginTop: theme.spacing.unit * 3,
        marginLeft: theme.spacing.unit,
        display: 'flex',
        justifyContent: 'center',
    },
});



class signuppage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            companyCode: '',
            userName: '',
            password: '',
            rePassWord: ''
        };
    }

    handleChange = (event) => {
        const {name , value} = event.target;
        this.setState({
            [name] : value
        })
    };

    render () {
        const { classes } = this.props;

        return (<React.Fragment>
            <SignUp updateval={this.handleChange.bind(this)} CC={this.state.companyCode} UN={this.state.userName} PW={this.state.password} REPW={this.state.rePassWord}/>

            {(this.state.password === this.state.rePassWord) && (this.state.password !== '') &&
            <Button
                variant="contained"
                color="primary"
                // onClick={this.handleNext}
                className={classes.button}
            >
                Sign Up!
            </Button>


            }

        </React.Fragment>)

    }

}

signuppage.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(signuppage);
