import React from 'react';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import withStyles from '@material-ui/core/styles/withStyles';

const styles = theme => ({
    header: theme.primary,
});

class signUp extends React.Component {


    render () {
        const { classes } = this.props;

 return <React.Fragment>
            <Typography variant="h6" gutterBottom align="center" className={classes.header}>
                Create account
             </Typography>
            <Grid container spacing={24}>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="companyCode"
                        name="companyCode"
                        label="companyCode"
                        fullWidth
                        autoComplete="companyCode"
                        onChange={this.props.updateval}
                        value={this.props.CC}
                    />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="userName"
                        name="userName"
                        label="userName"
                        fullWidth
                        autoComplete="usnm"
                        onChange={this.props.updateval}
                        value={this.props.UN}
                    />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="password"
                        name="password"
                        label="password"
                        fullWidth
                        type="password"
                        autoComplete="current-password"
                        onChange={this.props.updateval}
                        value={this.props.PW}
                    />
                </Grid>
                <Grid item xs={12}>
                    {this.props.PW === this.props.REPW ?
                        ( <TextField
                            required
                            id="rePassWord"
                            name="rePassWord"
                            label="rePassWord"
                            fullWidth
                            type="password"
                            autoComplete="current-password"
                            onChange={this.props.updateval}
                            value={this.props.REPW}
                        />) : (
                            <TextField
                                error
                                id="rePassWord"
                                name="rePassWord"
                                label="rePassWord"
                                fullWidth
                                type="password"
                                autoComplete="current-password"
                                onChange={this.props.updateval}
                                value={this.props.REPW}
                            />



                        )

                    }

                </Grid>
            </Grid>



        </React.Fragment>
    }

}

export default withStyles(styles)(signUp);
