import React, { useState }  from 'react';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Navbar from './../../components/Navbar';
import InputLabel from "../../components/PurposeSection/purpose";
import Select from "@material-ui/core/Select/Select";
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';


const styles = theme => ({
    layout: {
        width: 'auto',
        marginLeft: theme.spacing.unit * 2,
        marginRight: theme.spacing.unit * 2,
        [theme.breakpoints.up(600 + theme.spacing.unit * 2 * 2)]: {
            width: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
        },
    },
    paper: {
        marginTop: theme.spacing.unit * 3,
        marginBottom: theme.spacing.unit * 3,
        padding: theme.spacing.unit * 2,
        [theme.breakpoints.up(600 + theme.spacing.unit * 3 * 2)]: {
            marginTop: theme.spacing.unit * 6,
            marginBottom: theme.spacing.unit * 6,
            padding: theme.spacing.unit * 3,
        },
    },
    stepper: {
        padding: `${theme.spacing.unit * 3}px 0 ${theme.spacing.unit * 5}px`,
    },
    buttons: {
        display: 'flex',
        justifyContent: 'flex-end',
    },
    button: {
        marginTop: theme.spacing.unit * 3,
        marginLeft: theme.spacing.unit,
        display: 'flex',
        justifyContent: 'center',
    },
    formControl1: {
        margin: theme.spacing.unit,
        minWidth: 120,
    },
    margin: {
        margin: theme.spacing.unit,
    },
    textField: {
        flexBasis: 200,
    },
    paper1: {
        position: 'absolute',
        width: theme.spacing.unit * 50,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
        outline: 'none',
    },
});

const ranges = [{
    value: 1,
    label: 'Today'
    },{
    value: 2,
    label: 'MTD'
    },{
    value: 3,
    label: 'YTD'
    },{
    value: 4,
    label: 'Custom'
    }];

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

class report extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            period : "",
            purpose: "",
            open: false,
            modalOpen: false

        };
    }
    handleClose = () => {
        this.setState({ open: false });
    };

    openModal = () => {
        this.setState({open: true})
    };

    handleOpen = () => {
        this.setState({ open: true });
    };

    handleChange = (event, prop) => {
        const { value} = event.target;
        this.setState({
        [prop] : value
        })
    };

    render() {
        const { classes } = this.props;

        return (
            <React.Fragment>
                <CssBaseline />
                <Navbar handleSignOut={this.props.handleSignOut}/>
                <main className={classes.layout}>
                    <Paper className={classes.paper}>
                        <Typography component="h1" variant="h4" align="center">
                            Reporting
                        </Typography>
                        <React.Fragment>
                            <form autoComplete="off">
                                <FormControl className={classes.formControl1}>
                                {/*<InputLabel htmlFor="period">Period</InputLabel>*/}


                                    <TextField
                                        select
                                        className={classNames(classes.margin, classes.textField)}
                                        variant="outlined"
                                        label="Time Range"
                                        value={this.state.period}
                                        onChange={this.handleChange('period')}
                                        InputProps={{
                                            startAdornment: <InputAdornment position="start">Kg</InputAdornment>,
                                        }}
                                    >
                                        {ranges.map(option => (
                                            <MenuItem key={option.value} value={option.value}>
                                                {option.label}
                                            </MenuItem>
                                        ))}
                                    </TextField>


                            {/*<Select*/}
                                {/*open={this.state.open}*/}
                                {/*onClose={this.handleClose}*/}
                                {/*onOpen={this.handleOpen}*/}
                                {/*value={this.state.period}*/}
                                {/*onChange={this.handleChange}*/}
                                {/*inputProps={{*/}
                                    {/*name: 'period',*/}
                                    {/*id: 'period',*/}
                                {/*}}*/}
                                {/*label={'Time Period'}*/}
                            {/*>*/}
                                {/*<MenuItem value="">*/}
                                    {/*<em>None</em>*/}
                                {/*</MenuItem>*/}
                                {/*<MenuItem value={1}>Today</MenuItem>*/}
                                {/*<MenuItem value={2}>This Week</MenuItem>*/}
                                {/*<MenuItem value={3}>MTD</MenuItem>*/}
                                {/*<MenuItem value={4}>YTD</MenuItem>*/}
                                {/*<MenuItem value={4}>Custom</MenuItem>*/}

                            {/*</Select>*/}
                            </FormControl>
                            </form>
                            {/*<InputLabel htmlFor="purpose">Purpose</InputLabel>*/}
                            {/*<Select*/}
                                {/*open={this.state.open}*/}
                                {/*onClose={this.handleClose}*/}
                                {/*onOpen={this.handleOpen}*/}
                                {/*value={this.state.purpose}*/}
                                {/*onChange={this.handleChange}*/}
                                {/*inputProps={{*/}
                                    {/*name: 'purpose',*/}
                                    {/*id: 'purp',*/}
                                {/*}}*/}
                            {/*>*/}
                                {/*<MenuItem value="">*/}
                                    {/*<em>None</em>*/}
                                {/*</MenuItem>*/}
                                {/*<MenuItem value={1}>Receipt</MenuItem>*/}
                                {/*<MenuItem value={2}>Report</MenuItem>*/}
                                {/*<MenuItem value={3}>Consume</MenuItem>*/}
                                {/*<MenuItem value={4}>Error</MenuItem>*/}
                                {/*<MenuItem value={5}>Cycle Count</MenuItem>*/}
                                {/*<MenuItem value={6}>Production Receipt</MenuItem>*/}
                            {/*</Select>*/}


                                {(this.state.period != "" && this.state.purpose != "") && <Button
                                    variant="contained"
                                    color="primary"
                                    // onClick={}
                                    className={classes.button}
                                > 'Run Report' </Button>
                                }


                        </React.Fragment>
                    </Paper>
                </main>
                <div>
                    <Modal
                        aria-labelledby="simple-modal-title"
                        aria-describedby="simple-modal-description"
                        open={this.state.modalOpen}
                        onClose={this.handleClose}
                    >
                        <div style={getModalStyle()} className={classes.paper1}>
                            <Typography variant="h6" id="modal-title">
                                Error
                            </Typography>
                            <Typography variant="subtitle1" id="simple-modal-description">
                                One of the fields was left unfilled.
                            </Typography>
                        </div>
                    </Modal>
                </div>
            </React.Fragment>
        );
    }
}

report.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(report);
